css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;

}
.print-format{
    padding: 12px !important;
    margin-bottom: 0px;
    margin-top:0px;
}

html:
{%- for item in doc.items -%}
    {%- if item.batch_no -%}

        {% set batch %}{% endset %}
        {% if item.batch_no %}
            {%- set batch=frappe.get_doc("Batch",item.batch_no) -%}
        {% endif %}

        <table>
            <tr>
                <td>

                        {% if letter_head %}
                            {{ letter_head }}
                        {% endif %}

                </td>
            </tr>
        </table>
        <table>
            <tr>
                <td>
                    <br>

                    <div style="font-size: 16px;text-align: center; ">
                        <b>CERTIFICATE OF ANALYSIS</b>
                    </div>
                    <br>
                </td>
            </tr>
        </table>
        <div>
            <table>
                <tr>
                    <td style="width: 50%;">
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Product") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if item.item_name %}
                                    {{item.item_name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Customer Name") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.customer_name %}
                                    {{doc.customer_name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Invoice No") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.name %}
                                    {{doc.name}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Dated") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if doc.posting_date %}
                                    {{doc.get_formatted("posting_date")}}
                                {% endif %}
                            </div>
                        </div>
                    </td>
                    <td style="width: 50%;">
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("BATCH NO") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;
                                {% if item.batch_no %}
                                    {{item.batch_no}}
                                {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Date of Mfg.") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;{% if batch.manufacturing_date %}
                                            {{ batch.get_formatted("manufacturing_date") }}
                                        {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Date of Exp.") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;{% if batch.expiry_date %}
                                            {{ batch.get_formatted("expiry_date") }}
                                        {% endif %}
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-xs-4">
                                <b>{{ _("Quantity") }}</b>
                            </div>
                            <div class="col-xs-8">
                                :&nbsp;{% if item.total_weight %}{{item.total_weight}}{% endif %}&nbsp;{% if item.weight_uom %}{{item.weight_uom}}{% endif %}
                            </div>
                        </div>
                    </td>
                </tr>
            </table>
            <table>
                <tr>
                    <th style="width: 5%;"><b>{{ _("Sr No.") }}</b></th>
                    <th style="width: 35%;"><b>{{ _("Test Parameters") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Specification") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Result") }}</b></th>
                    <th style="width: 20%;"><b>{{ _("Remark") }}</b></th>
                </tr>
                {% for i in batch.test_result %}
                    <tr>
                        <td>{{i.idx}}</td>
                        <td>
                            {% if i.specification %}
                                {{i.specification}}
                            {% endif %}
                        </td>
                        <td>
                            {% if i.reading_value %}
                                {{i.reading_value}}
                            {% endif %}
                        </td>
                        <td>
                            {% if i.reading1 %}
                                {{i.reading1}}
                            {% endif %}
                        </td>
                        <td>
                            {% if i.status %}
                                {{i.status}}
                            {% endif %}
                        </td>
                    </tr>
                {% endfor %}

            </table>
            <table>
                <tr>
                    <td style="width: 80%;">
                        <b>
                            The product confirms to the specification and is of standard quality.
                        </b>
                    </td>
                    <td style="width: 20%; text-align: center; ">
                        <br><br>
                        {% if batch.owner %}
                            {{batch.owner}}
                        {% endif %}<br>
                        Tested by
                    </td>
                </tr>
            </table>
        </div>

        <div style="font-size: 12px;">
            This is computer generated Certificate of Analysis, hence no signature is required.
        </div>
        <div class="page-break"></div>
    {%- endif -%}
{%- endfor -%}
